"use strict";document.querySelectorAll(".tabs").forEach(t=>{t.addEventListener("click",s=>{if(s.target.classList.contains("tabs__main-link")||s.target.classList.contains("tabs__inline-link")){s.stopPropagation();const e=t.querySelectorAll(".tabs__main-link"),a=t.querySelectorAll(".tabs__content"),i=t.querySelectorAll(".tabs__inline-link");let n;n=s.target.closest(".tabs__list")?[...e].indexOf(s.target):[...a].indexOf(s.target.parentElement);for(const t of a)t.classList.remove("tabs__content--active");a[n].classList.add("tabs__content--active");for(const t of e)t.classList.remove("tabs__main-link--active");e[n].classList.add("tabs__main-link--active");for(const t of i)t.classList.remove("tabs__inline-link--active");i[n].classList.add("tabs__inline-link--active")}})});